import React, { useState, useEffect } from 'react';
import { fetchCourts, calculatePrice, createBooking } from '../services/bookingService';
// Import your Coach and Equipment fetching services here too

const initialBookingState = {
    date: new Date().toISOString().split('T')[0],
    courtId: '',
    startTime: '10:00',
    endTime: '11:00',
    coachId: '',
    rackets: 0,
    shoes: 0,
};

function BookingPage() {
    const [booking, setBooking] = useState(initialBookingState);
    const [courts, setCourts] = useState([]);
    const [priceBreakdown, setPriceBreakdown] = useState(null);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    
    // --- 1. Fetch Initial Data (Courts) ---
    useEffect(() => {
        fetchCourts().then(setCourts).catch(console.error);
        // Also fetch coaches, etc.
    }, []);

    // --- 2. Live Price Calculation Hook (PHASE 4 CALL) ---
    useEffect(() => {
        // Debounce this function in a real app to avoid excessive API calls!
        if (booking.courtId && booking.startTime && booking.endTime) {
            setLoading(true);
            const bookingDetails = {
                courtId: booking.courtId,
                start: `${booking.date}T${booking.startTime}:00`,
                end: `${booking.date}T${booking.endTime}:00`,
                coachId: booking.coachId,
                requestedResources: { rackets: booking.rackets, shoes: booking.shoes },
                // userId should be retrieved from authentication context
            };
            
            calculatePrice(bookingDetails)
                .then(data => {
                    setPriceBreakdown(data);
                    setError(null);
                })
                .catch(err => {
                    console.error("Pricing Error:", err);
                    setPriceBreakdown(null);
                    setError("Could not calculate price.");
                })
                .finally(() => setLoading(false));
        } else {
            setPriceBreakdown(null);
        }
    }, [booking]); // Recalculate whenever booking state changes

    const handleChange = (e) => {
        const { name, value, type } = e.target;
        setBooking(prev => ({
            ...prev,
            [name]: type === 'number' ? parseInt(value) : value
        }));
    };

    // --- 3. Submission Handler (PHASE 3 CALL) ---
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);
        
        try {
            const bookingDetailsToSend = {
                ...booking,
                // Format dates for backend consistency
                start: `${booking.date}T${booking.startTime}:00`, 
                end: `${booking.date}T${booking.endTime}:00`,
                requestedResources: { rackets: booking.rackets, shoes: booking.shoes },
                // Include userId here
            };

            await createBooking(bookingDetailsToSend);
            alert('Booking confirmed successfully!');
            setBooking(initialBookingState);
        } catch (err) {
            setError(err.message || 'Failed to complete booking. Check availability.');
        } finally {
            setLoading(false);
        }
    };

    // --- 4. Render UI ---
    return (
        <div className="p-8 max-w-2xl mx-auto">
            <h1 className="text-3xl font-bold mb-6">Court Booking</h1>
            {error && <div className="bg-red-100 text-red-700 p-3 mb-4 rounded">{error}</div>}
            
            <form onSubmit={handleSubmit} className="space-y-4">
                {/* Date and Time Selector (Time Slot Selector) */}
                <input type="date" name="date" value={booking.date} onChange={handleChange} required />
                <input type="time" name="startTime" value={booking.startTime} onChange={handleChange} required />
                <input type="time" name="endTime" value={booking.endTime} onChange={handleChange} required />

                {/* Court Selector */}
                <select name="courtId" value={booking.courtId} onChange={handleChange} required className="w-full p-2 border rounded">
                    <option value="">Select a Court</option>
                    {courts.map(court => (
                        <option key={court._id} value={court._id}>{court.name} ({court.type})</option>
                    ))}
                </select>

                {/* Coach and Equipment Selectors */}
                {/* You would populate this with data from your Coaches and Equipment endpoints */}
                <label className="block">
                    Add Rackets:
                    <input type="number" name="rackets" value={booking.rackets} onChange={handleChange} min="0" className="ml-2 border rounded p-1" />
                </label>
                <label className="block">
                    Add Coach:
                    <select name="coachId" value={booking.coachId} onChange={handleChange} className="ml-2 border rounded p-1">
                         <option value="">No Coach</option>
                         {/* Map coaches here */}
                    </select>
                </label>

                {/* Live Price Display (PHASE 4 Output) */}
                <div className="p-4 border-t-2 border-gray-200">
                    <h2 className="text-xl font-semibold mb-2">Live Price Breakdown</h2>
                    {loading && <p>Calculating price...</p>}
                    {priceBreakdown ? (
                        <ul className="text-gray-700">
                            <li>Court Fee: ${priceBreakdown.baseCourtFee.toFixed(2)}</li>
                            {priceBreakdown.baseCoachFee > 0 && <li>Coach Fee: ${priceBreakdown.baseCoachFee.toFixed(2)}</li>}
                            {priceBreakdown.equipmentFee > 0 && <li>Equipment Fee: ${priceBreakdown.equipmentFee.toFixed(2)}</li>}
                            {priceBreakdown.rulesFee !== 0 && <li>*Rules Modifier: ${priceBreakdown.rulesFee.toFixed(2)}</li>}
                            <li className="font-bold text-lg text-green-600">Total: ${priceBreakdown.total.toFixed(2)}</li>
                        </ul>
                    ) : <p>Select court and time to see price.</p>}
                </div>

                <button 
                    type="submit" 
                    disabled={loading || !priceBreakdown} 
                    className="w-full bg-blue-500 text-white p-3 rounded hover:bg-blue-600 disabled:bg-gray-400"
                >
                    {loading ? 'Processing...' : 'Book Court'}
                </button>
            </form>
        </div>
    );
}

export default BookingPage;
