import React, { useState } from 'react';
import SlotSelector from '../components/SlotSelector';
import BookingForm from '../components/BookingForm'; // Assuming you will build this next

const BookingPage = () => {
    // State to hold the selected court, time, and date
    const [bookingDetails, setBookingDetails] = useState(null);

    // This function receives the selected slot data from SlotSelector
    const handleSlotSelection = (selection) => {
        setBookingDetails(selection);
    };

    return (
        <div className="container mx-auto p-6 bg-gray-50 min-h-screen">
            <h1 className="text-4xl font-extrabold mb-8 text-center text-indigo-700">
                Court Booking Center
            </h1>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Slot Selector takes up 2/3 of the width */}
                <div className="lg:col-span-2">
                    <SlotSelector onSelectSlot={handleSlotSelection} />
                </div>

                {/* Booking Form/Summary takes up 1/3 of the width */}
               <div className="lg:col-span-1">
                    <div className="p-6 bg-white rounded-lg shadow-xl sticky top-6">
                        <h2 className="text-2xl font-semibold mb-4 text-gray-800">Booking Summary</h2>
                        {bookingDetails ? (
                            <>
                                {/* Removed repetitive court info here as it's now in the form */}
                                <BookingForm bookingDetails={bookingDetails} /> 
                            </>
                        ) : (
                            <p className="text-gray-500">Please select an available slot above.</p>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default BookingPage;
