import React, { useState, useEffect } from 'react';
// ASSUMPTION: You need a fetchCoaches function from your service file
import { fetchCourts, fetchCoaches, calculatePrice, createBooking } from '../services/bookingService'; 

const initialBookingState = {
    date: new Date().toISOString().split('T')[0],
    courtId: '',
    startTime: '10:00',
    endTime: '11:00',
    coachId: '',
    rackets: 0,
    shoes: 0,
};

function BookingPage() {
    const [booking, setBooking] = useState(initialBookingState);
    const [courts, setCourts] = useState([]);
    const [coaches, setCoaches] = useState([]); // <-- NEW: State for Coaches
    const [priceBreakdown, setPriceBreakdown] = useState(null);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    
    // --- 1. Fetch Initial Data (Courts & Coaches) ---
    useEffect(() => {
        // Fetch Courts (This is working)
        fetchCourts().then(setCourts).catch(console.error);

        // <-- NEW CODE BLOCK: Fetch Coaches -->
        // We will assume you implement fetchCoaches in bookingService.js
        fetchCoaches()
            .then(data => {
                console.log("Coaches loaded:", data); // Check the console for this!
                setCoaches(data);
            })
            .catch(err => console.error("Error fetching coaches:", err));
        // <-- END NEW CODE BLOCK -->
        
    }, []);

    // --- 2. Live Price Calculation Hook (PHASE 4 CALL) ---
    useEffect(() => {
        // Debounce this function in a real app to avoid excessive API calls!
        if (booking.courtId && booking.startTime && booking.endTime) {
            setLoading(true);
            const bookingDetails = {
                courtId: booking.courtId,
                start: `${booking.date}T${booking.startTime}:00`,
                end: `${booking.date}T${booking.endTime}:00`,
                coachId: booking.coachId,
                requestedResources: { rackets: booking.rackets, shoes: booking.shoes },
                // userId should be retrieved from authentication context
            };
            
            calculatePrice(bookingDetails)
                .then(data => {
                    setPriceBreakdown(data);
                    setError(null);
                })
                .catch(err => {
                    console.error("Pricing Error:", err);
                    setPriceBreakdown(null);
                    setError("Could not calculate price.");
                })
                .finally(() => setLoading(false));
        } else {
            setPriceBreakdown(null);
        }
    }, [booking]); // Recalculate whenever booking state changes

    const handleChange = (e) => {
        const { name, value, type } = e.target;
        setBooking(prev => ({
            ...prev,
            // Convert to number only if the type attribute is set to 'number' in JSX
            [name]: type === 'number' ? parseInt(value) || 0 : value
        }));
    };

    // --- 3. Submission Handler (PHASE 3 CALL) ---
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);
        
        try {
            const bookingDetailsToSend = {
                ...booking,
                // Format dates for backend consistency
                start: `${booking.date}T${booking.startTime}:00`, 
                end: `${booking.date}T${booking.endTime}:00`,
                requestedResources: { rackets: booking.rackets, shoes: booking.shoes },
                // Include a placeholder userId here until authentication is built
                userId: "60c72b1f9c8d1d0015b4c10a" // Replace with a valid ID from your users collection
            };

            await createBooking(bookingDetailsToSend);
            alert('Booking confirmed successfully!');
            setBooking(initialBookingState);
        } catch (err) {
            setError(err.message || 'Failed to complete booking. Check availability.');
        } finally {
            setLoading(false);
        }
    };

    // --- 4. Render UI ---
    return (
        <div className="p-4 border-t-2 border-gray-200">
        <h2 className="text-xl font-semibold mb-2">Live Price Breakdown</h2>
        {loading && <p>Calculating price...</p>}
        {/* The outer check (priceBreakdown ?) is correct, but we need inner checks too */}
        {priceBreakdown ? (
            <ul className="text-gray-700">
                {/* FIX: Use optional chaining (?.) on every property before .toFixed(2) */}
                <li>Court Fee: ${priceBreakdown.baseCourtFee?.toFixed(2) || '0.00'}</li> 
                
                {priceBreakdown.baseCoachFee > 0 && 
                  <li>Coach Fee: ${priceBreakdown.baseCoachFee?.toFixed(2) || '0.00'}</li>}
                
                {priceBreakdown.equipmentFee > 0 && 
                  <li>Equipment Fee: ${priceBreakdown.equipmentFee?.toFixed(2) || '0.00'}</li>}
                
                {priceBreakdown.rulesFee !== 0 && 
                  <li>*Rules Modifier: ${priceBreakdown.rulesFee?.toFixed(2) || '0.00'}</li>}
                
                {/* CRITICAL FIX for the Total line: */}
                <li className="font-bold text-lg text-green-600">
                    Total: ${priceBreakdown.total?.toFixed(2) || '0.00'}
                </li>
            </ul>
        ) : <p>Select court and time to see price.</p>}
    </div>
    );
}

export default BookingPage;
