const API_BASE_URL = 'http://localhost:5000';// Adjust port/URL as needed

export const fetchCourts = async () => {
    const response = await fetch(`${BASE_URL}/courts`);
    return response.json();
};

const calculatePrice = async (bookingDetails) => {
    // FIX THE URL: Change /api/calculate-price to /api/bookings/calculate-price
    const response = await fetch(`${API_BASE_URL}/api/bookings/calculate-price`, { 
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(bookingDetails),
    });

    if (!response.ok) {
        throw new Error('Price calculation failed.');
    }

    return response.json();
};

export const createBooking = async (bookingDetails) => {
    const response = await fetch(`${BASE_URL}/bookings`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(bookingDetails),
    });
    // The backend handles the availability check and returns 409 if unavailable.
    if (!response.ok) {
        // Catch 409 availability conflicts or other errors
        const errorData = await response.json();
        throw new Error(errorData.message || 'Booking failed due to a server error.');
    }
    return response.json();
};
export const fetchCoaches = async () => {
    const response = await fetch('http://localhost:5000/api/coaches');
    if (!response.ok) {
        throw new Error('Failed to fetch coaches from API');
    }
    return response.json();
};
